mapper.update:
UPDATE warehouse_item
SET item_amount = #{itemAmount},
    update_date = SYSDATE
WHERE product_id = #{productId}
AND warehouse_id = #{warehouseId}

mapper.select:
SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM product
WHERE product_id = #{productId}

mapper.insert:
INSERT INTO product (
    product_id, product_name, product_type, unit, spec, emp_id, created_at
) VALUES (
   #{productId}, #{productName}, '부품', #{unit}, #{spec}, #{empId}, SYSDATE
)

mapper.delete:
DELETE FROM product
WHERE product_id IN

    #{id}

mapper.select:
SELECT emp_name 
FROM employee 
WHERE emp_id = #{empId}

SELECT COUNT(*) FROM (
    SELECT 1 FROM input 
    WHERE product_id = #{productId} 
    AND in_complete > ADD_MONTHS(SYSDATE, -1)
    UNION ALL
    SELECT 1 FROM output 
    WHERE product_id = #{productId} 
    AND out_complete > ADD_MONTHS(SYSDATE, -1)  
)

mapper.delete:
DELETE FROM product
WHERE product_id IN

    #{id}

mapper.select:
SELECT NVL(SUM(item_amount), 0)  
FROM warehouse_item 
WHERE product_id = #{productId} 
AND warehouse_id = #{warehouseId}
AND use_yn = 'Y'

mapper.update:
UPDATE warehouse_item 
SET item_amount = CASE 
    WHEN (#{newQty} / (SELECT COUNT(*) FROM warehouse_item 
                      WHERE product_id = #{productId} 
                      AND warehouse_id = #{warehouseId})) > 0
    THEN #{newQty} / (SELECT COUNT(*) FROM warehouse_item 
                     WHERE product_id = #{productId} 
                     AND warehouse_id = #{warehouseId})
    ELSE item_amount
END
WHERE product_id = #{productId} 
AND warehouse_id = #{warehouseId}
AND use_yn = 'Y'

UPDATE product 
SET quantity = #{totalQty}
WHERE product_id = #{productId}

UPDATE warehouse_item
SET item_amount = #{newQty}
WHERE product_id = #{productId}
AND warehouse_id = #{warehouseId}
AND location_id = #{locationId}

mapper.delete:
DELETE FROM warehouse_item
WHERE product_id = #{productId}
AND warehouse_id = #{warehouseId}
AND location_id = #{locationId}
