mapper.update:
UPDATE warehouse_item
SET item_amount = item_amount + #{addAmount},
    update_date = SYSDATE
WHERE location_id = #{locationId}
AND material_id = #{materialId}

mapper.insert:
INSERT INTO warehouse_item (
    manage_id, warehouse_id, material_id, item_amount,
    use_yn, create_date, location_id, emp_id
) 
SELECT 
    #{manageId}, #{warehouseId}, #{materialId}, #{itemAmount},
    'Y', SYSDATE, #{locationId}, #{empId}
FROM dual
WHERE NOT EXISTS (
    SELECT 1 FROM warehouse_item 
    WHERE location_id = #{locationId} 
    AND material_id = #{materialId}
)

mapper.update:
UPDATE warehouse_item
SET item_amount = item_amount + #{itemAmount},
    update_date = SYSDATE
WHERE location_id = #{locationId} 
AND material_id = #{materialId}

mapper.select:
SELECT o.out_id as "outId", o.product_id as "productId", o.material_id as "materialId", o.out_count as "outCount", o.out_type as "outType", o.batch_id as "batchId", TO_CHAR(o.out_complete, 'YYYY-MM-DD HH24:MI') as "outDate" FROM output o WHERE 1=1
AND o.out_type = #{outType}
AND o.out_type = #{outStatus}
ORDER BY o.out_id DESC

SELECT CASE WHEN COUNT(*) > 0 THEN 1 ELSE 0 END
FROM material
WHERE material_id = #{productId}

mapper.update:
UPDATE warehouse_item
SET item_amount = item_amount - #{qty},
    update_date = SYSDATE
WHERE product_id = #{productId}
AND warehouse_id = #{warehouseId}
AND location_id = #{locationId}

UPDATE warehouse_item
SET item_amount = item_amount - #{qty},
    update_date = SYSDATE
WHERE material_id = #{materialId}
AND warehouse_id = #{warehouseId}
AND location_id = #{locationId}

UPDATE product
SET quantity = quantity - #{qty}
WHERE product_id = #{productId}

UPDATE material
SET quantity = quantity - #{qty}
WHERE material_id = #{materialId}
